CREATE TABLE Employees 			(EmployeesID INT Identity (1,1) NOT NULL, FullName NVARCHAR(30) NOT NULL, Title NVARCHAR(30) NOT NULL, CourtesyTitle NVARCHAR(10) NOT NULL, Address NVARCHAR(50) NOT NULL, 			City NVARCHAR(20) NOT NULL, PostalCode NVARCHAR(10) NOT NULL, Country NVARCHAR(20) NOT NULL, BirthDate DATE NOT NULL, Photo IMAGE NULL, HireDate DATE NOT NULL,			PRIMARY KEY (EmployeesID));CREATE TABLE Customers 			(CustomersID INT Identity (1,1) NOT NULL, FullName NVARCHAR(30) NOT NULL, Title NVARCHAR(10) NOT NULL, CC NVARCHAR(20) NOT NULL UNIQUE, NIF NVARCHAR(30) NOT NULL UNIQUE,			Address NVARCHAR(50) NOT NULL, City NVARCHAR(20) NOT NULL, PostalCode NVARCHAR(10) NOT NULL, Country NVARCHAR(20) NOT NULL, Fax NVARCHAR(20) NOT NULL, 			PRIMARY KEY (CustomersID)); CREATE TABLE Products 			(ProductsID INT Identity (1,1) NOT NULL, ProductName NVARCHAR(30) NOT NULL UNIQUE, UnitPrice DECIMAL, InStock INTEGER NOT NULL, 			PRIMARY KEY (ProductsID));CREATE TABLE Shippers (ShippersID INT Identity (1,1) NOT NULL, ShippersName NVARCHAR(30), ShippersPhone NVARCHAR(30), 			PRIMARY KEY (ShippersID));CREATE TABLE Orders (OrdersID INT Identity (1,1) NOT NULL,  CustomersID INT NOT NULL, EmployeesID  INT NOT NULL, ShippersID  INT NOT NULL, Date DATE NOT NULL,			PRIMARY KEY (OrdersID), 			FOREIGN KEY (ShippersID) REFERENCES Shippers (ShippersID), 			FOREIGN KEY (CustomersID) REFERENCES Customers (CustomersID),			FOREIGN KEY (EmployeesID) REFERENCES Employees (EmployeesID));CREATE TABLE OrderDetails (OrderDetailsID INT Identity (1,1) NOT NULL, OrdersID INT NOT NULL, ProductsID INT NOT NULL,			PRIMARY KEY (OrderDetailsID), 			FOREIGN KEY (OrdersID) REFERENCES Orders (OrdersID), 			FOREIGN KEY (ProductsID) REFERENCES Products (ProductsID));			